// Este código unifica o estado da máquina.
// Ele espera receber msg.payload como "rodando" ou "desligada" dos botões.

let novoEstado = msg.payload;

if (novoEstado === "rodando") {
    flow.set("machine_state", "rodando");
    node.warn("MÁQUINA LIGADA MANUALMENTE.");
} else if (novoEstado === "desligada") {
    flow.set("machine_state", "desligada");
    node.warn("MÁQUINA DESLIGADA MANUALMENTE.");
}

// Este nó não precisa enviar nenhuma mensagem adiante.
return null;

// --- VERIFICAÇÃO DE ESTADO "INTELIGENTE" ---
let currentState = flow.get("machine_state") || "desligada";
let lastState = flow.get("last_checked_state") || "desligada";

if (currentState !== "rodando") {
    if (currentState !== lastState) {
        node.warn("Ciclo ignorado. Estado atual: " + currentState);
        flow.set("last_checked_state", currentState);
    }
    return null;
}

if (lastState !== "rodando") {
    flow.set("last_checked_state", "rodando");
}

// --- PROBABILIDADES (AJUSTADO) ---
const taxaDeSucesso = 0.89;       // 89% de chance
const taxaDeMicroParada = 0.10;   // 10% de chance
// O 'else' cuidará do 1% restante (Parada Longa)

const aleatorio = Math.random();

// --- LÓGICA DE DECISÃO ---

if (aleatorio < taxaDeSucesso) {
    // SUCESSO (89%): Envia pela primeira saída
    return [{ payload: { status: "produzido" } }, null, null];

} else if (aleatorio < taxaDeSucesso + taxaDeMicroParada) {
    // MICRO-PARADA (10%): Envia pela segunda saída
    const msgMicroParada = { payload: { status: "parada", motivo: "Micro-parada", duration_seconds: 5 } };
    return [null, msgMicroParada, null];

} else {
    // PARADA LONGA (1%): A LÓGICA PRINCIPAL ACONTECE AQUI
    flow.set("machine_state", "parada");

    node.warn("PARADA LONGA INICIADA! Produção pausada.");
    flow.set("last_checked_state", "parada");

    // --- DURAÇÃO (AJUSTADO) ---
    // Gera a duração aleatória (entre 10 e 60 segundos)
    const duracao = Math.floor(Math.random() * (60 - 10 + 1)) + 10;

    // Prepara a mensagem para salvar a parada no banco (Saída 2)
    const msgParadaDB = { payload: { status: "parada", motivo: "Parada Não Classificada", duration_seconds: duracao } };

    // Prepara uma mensagem de "reinício" com um delay (Saída 3)
    const msgReinicio = {
        payload: "Sinal para reiniciar a máquina",
        "delay": duracao * 1000 // milissegundos
    };

    return [null, msgParadaDB, msgReinicio];
}

let isDefective = Math.random() < 0.03;
let defectivePieces = flow.get("defectivePieces") || 0;
if (isDefective) {
    defectivePieces++;
    flow.set("defectivePieces", defectivePieces);
}

msg.payload = {
    machineId: "Torno",
    type: "production_cycle",
    isDefective: isDefective,
    totalPieces: 1,
    timestamp: new Date()
};
msg.collection = "ciclos_producao";
return msg;

const motivo = msg.payload.motivo;
const duracao = msg.payload.duration_seconds;

msg.payload = {
    machineId: "Torno",
    type: "machine_stop",
    reason: motivo,
    duration_seconds: duracao,
    classified: (motivo === "Parada Não Classificada" ? false : true),
    timestamp: new Date()
};
msg.collection = "paradas_maquina";
return msg;

let machineState = flow.get("machine_state");

if (machineState === "parada") {
    flow.set("machine_state", "rodando");
    node.warn("Máquina reiniciada. Produção retomada.");
} else {
    node.warn("Reinício automático ignorado. Estado atual: " + machineState);
}
return msg;

// --- PARÂMETROS DA SIMULAÇÃO ---
const machineId = "Torno";

// --- FUNÇÃO PARA GERAR NÚMEROS ALEATÓRIOS EM UMA FAIXA ---
function randomInRange(min, max, decimals = 1) {
    const str = (Math.random() * (max - min) + min).toFixed(decimals);
    return parseFloat(str);
}

// --- GERAÇÃO DAS MÉTRICAS DE REDE ---
let rssi = randomInRange(-65, -40, 0);       // Sinal forte a médio
let snr = randomInRange(20, 35, 0);           // Boa relação sinal-ruído
let latency = randomInRange(10, 50, 0);       // Latência baixa a média
let throughput = randomInRange(40, 60, 1);    // Vazão de dados
let jitter = randomInRange(1, 5, 1);          // Jitter baixo
let packetLoss = randomInRange(0, 1.5, 1);    // Perda de pacote baixa

// --- LÓGICA PARA DETERMINAR O STATUS ---
let status = "online";

// Simula uma perda de sinal baseada no RSSI
if (rssi < -60) {
    status = "weak_signal";
    // Com sinal fraco, as outras métricas pioram
    latency += 30;
    jitter += 5;
    packetLoss += 2;
}

// Simula uma queda completa de rede (5% de chance)
if (Math.random() < 0.05) {
    status = "offline";
    // Se está offline, as métricas são nulas ou indicam falha
    rssi = null;
    snr = null;
    latency = null;
    throughput = null;
    jitter = null;
    packetLoss = 100.0;
}


// --- MONTAGEM DO OBJETO FINAL ---
msg.payload = {
    machineId: machineId,
    timestamp: new Date().toISOString(), // Formato padrão universal Z (UTC)
    networkMetrics: {
        rssi: rssi,             // dBm
        snr: snr,               // dB
        latency: latency,       // ms
        throughput: throughput, // Mbps
        jitter: jitter,         // ms
        packetLoss: packetLoss  // %
    },
    status: status,           // online/offline/weak_signal
    lastUpdate: "Agora"
};

return msg;

// O critério para encontrar a máquina.
const query = {
    machineId: "Torno"
};

// Os dados que queremos inserir ou atualizar.
const update = {
    $set: {
        name: "Simulador Interno Node-RED",
        status: "registrada",
        location: "Planta Piloto TCC",
        registeredAt: new Date(),
        // Adicionando o machineId aqui também para garantir que ele seja criado na primeira vez
        machineId: "SIMULADOR-01"
    }
};

// As opções da operação, incluindo o upsert.
const options = {
    upsert: true
};

// O formato correto para a operação 'updateOne' neste nó:
// um array contendo [query, update, options]
msg.payload = [
    query,
    update,
    options
];

// As propriedades msg.query, msg.options etc. não são mais necessárias
// pois está tudo dentro do msg.payload.
delete msg.query;
delete msg.options;

return msg;
